import { useState } from "react";

function App() {
  //TODO: Presentar el concepto STATE
//hooks

const [inputState, setInputState] = useState({
  titulo: "",
  fecha: "",
  nota: "",
});


  const handleInputChange = (event) => {
    setInputState({
      ...inputState,
      [event.target.name]: event.target.value,

    });
  };




  return (
    <div className="App">
      <h3>Notas</h3>
      <label htmlFor="titulo">Input titulo</label>
      <input 
       id="titulo" 
       name="titulo" 
       type="text" 
       onChange={handleInputChange}
       value={inputState}/>


      <label htmlFor="fecha">Input fecha</label>
      <input 
       id="fecha" 
       name="fecha" 
       type="text" 
       onChange={handleInputChange}
       value={inputState}/>


      <label htmlFor="nota">Input nota</label>
      <input 
       id="nota" 
       name="nota" 
       type="text" 
       onChange={handleInputChange}
       value={inputState}/>
    </div>
  );
}

export default App;
