import { useState } from "react";

function App() {
  //TODO: Presentar el concepto STATE
//hooks

const [tituloState, setTituloState] = useState("");
const [fechaState, setFechaState] = useState("");
const [notaState, setNotaState] = useState("");

  const handleChangeTitulo = (event) => {
    setTituloState(event.target.value);
  };

  const handleChangeFecha = (event) => {
    setFechaState(event.target.value);
  };

  const handleChangeNota = (event) => {
    setNotaState(event.target.value);
  };


  return (
    <div className="App">
      <h3>Notas</h3>
      <label htmlFor="titulo">Input titulo</label>
      <input 
       id="titulo" 
       name="titulo" 
       type="text" 
       onChange={handleChangeTitulo}
       value={tituloState}/>


      <label htmlFor="fecha">Input fecha</label>
      <input 
       id="fecha" 
       name="fecha" 
       type="text" 
       onChange={handleChangeFecha}
       value={fechaState}/>


      <label htmlFor="nota">Input nota</label>
      <input 
       id="nota" 
       name="nota" 
       type="text" 
       onChange={handleChangeNota}
       value={notaState}/>
    </div>
  );
}

export default App;
